ar: SnakeNavigationBar.color(        height: 60,        behaviour: snakeBarStyle,        snakeShape: snakeShape,        shape: bottomBarShape,        elevation: 50,        padding: padding,        snakeViewColor: selectedColor,        selectedItemColor:        snakeShape == SnakeShape.indicator ? selectedColor : null,        shadowColor: Colors.green,        backgroundColor: Colors.white,        unselectedItemColor: unselectedColor,        showUnselectedLabels: showUnselectedLabels,        showSelectedLabels: showSelectedLabels,        currentIndex: _selectedItemPosition,        onTap: (index) => setState(() {          _selectedItemPosition = index;          print(index);          switch (index) {            case 0:              print("_selectedIndex=> ${index}");              break;            case 1:              print("_selectedIndex=> ${index}");              break;            case 2:              print("_selectedIndex=> ${index}");              break;            case 3:              print("_selectedIndex=> ${index}");              break;          }        }),        items: const [          BottomNavigationBarItem(            icon: Icon(Icons.home_filled),            label: 'home',          ),          BottomNavigationBarItem(              icon: Icon(Icons.favorite,),              label: 'favorite'),          BottomNavigationBarItem(              icon: Icon(Icons.shopping_cart_outlined),              label: 'cart'),          BottomNavigationBarItem(              icon: Icon(UniconsLine.user),              label: 'profile')        ],        selectedLabelStyle: const TextStyle(fontSize: 14),        unselectedLabelStyle: const TextStyle(fontSize: 10),      ),    );  }}