import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class Text_Field1 extends StatelessWidget {  const Text_Field1({    Key? key,    required this.MyController,    required this.fintText,    required this.icon,    required this.labelText, required this.pass,  }) : super(key: key);  final TextEditingController MyController;  final String fintText;  final String labelText;  final Widget icon;  final bool pass ;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(bottom: 20),      child: SizedBox(          width: 340,          height: 50,          child: TextField(            obscureText: pass,            controller: MyController,            decoration: InputDecoration(              hintText: fintText,              labelText: labelText,              suffixIcon: icon,              enabledBorder: OutlineInputBorder(                borderSide:                const BorderSide(width: 2, color: Color(0xffE4F7FF)),                borderRadius: BorderRadius.circular(20.0),              ),              focusedBorder: OutlineInputBorder(                  borderSide:                  const BorderSide(width: 2, color: Color(0xff45B9EE)),                  borderRadius: BorderRadius.circular(20.0),                  gapPadding: 2.0),              errorBorder: OutlineInputBorder(                borderSide: BorderSide(width: 2, color: Colors.red),                borderRadius: BorderRadius.circular(20.0),              ),            ),          )),    );  }}