d_List(String text) async{    relatidList = BackEnd.Prodects3.where((e) => e.category.title == text).toList();  }  makeAsFav() {    if (isFav == false)      isFav = true;    else      isFav = false;  }  deleteFromCart() {    if (delete_from_cart == false)      delete_from_cart = true;    else      delete_from_cart = false;  }  makeAsClicked() {    if (is_clicked == false)      is_clicked = true;    else      is_clicked = false;  }  addToCart() {    if (add == false)      add = true;    else      add = false;  }  static List Prodects_cart = <Cart_modles>[];  static String token = Database2.box.read("token");  static Get_cart() async {    String token = Database2.box.read("token");    try {      final res = await http.get(        Uri.parse(ApiConstants.get_Cart),        // Send authorization headers to the backend.        headers: {          HttpHeaders.authorizationHeader:              'Bearer $token',        },      );      final responseJson = json.decode(res.body);      responseJson.forEach((e) {        Prodects_cart.add(Cart_modles.fromJson(e));      });      print(res.statusCode);      if (res.statusCode == 200 || res.statusCode == 201) {        print("//////////////////////////////");         print(Prodects_cart[0].product.title);      }    } catch (e) {      print("error");      print(e);    }  }  static add_to_card({required int id}) async {    var headers = {      'Authorization': 'Bearer $token',      'Content-Type': 'text/plain'    };    var request = http.Request('POST', Uri.parse(ApiConstants.add_to_Cart));    request.body = '''{\r\n  "product_id":${id},\r\n  "quantity": 1\r\n}''';    request.headers.addAll(headers);    http.StreamedResponse response = await request.send();    if (response.statusCode == 200) {      print(await response.stream.bytesToString());    }    else {    print(response.reasonPhrase);    }  }  static delete_from_card(int id) async{    var headers = {      'Authorization': 'Bearer $token',      'Content-Type': 'text/plain'    };    var request = http.Request('DELETE', Uri.parse('https://zaynhamza.pythonanywhere.com/api/cart/item/${id}'));    request.body = '''{\r\n  "detail": "string"\r\n}''';    request.headers.addAll(headers);    http.StreamedResponse response = await request.send();    if (response.statusCode == 200) {      print(await response.stream.bytesToString());    }    else {      print(response.reasonPhrase);    }  }  static create() async {    var headers = {      'Authorization': 'Bearer $token'    };    var request = http.Request('POST', Uri.parse(ApiConstants.create));    request.body = '''''';    request.headers.addAll(headers);    http.StreamedResponse response = await request.send();    if (response.statusCode == 200) {      print(await response.stream.bytesToString());    }    else {    print(response.reasonPhrase);    }  }  static checkout() async {    var headers = {      'Authorization': 'Bearer $token'    };    var request = http.Request('POST', Uri.parse(ApiConstants.checkout));    request.body = '''''';    request.headers.addAll(headers);    http.StreamedResponse response = await request.send();    if (response.statusCode == 200) {      print(await response.stream.bytesToString());    }    else {    print(response.reasonPhrase);    }  }}