class Api_models {  int? id;  String? title;  String? banner;  String? description;  Category? category;  List<Colors>? colors;  int? price;  bool? isAvailable;  bool? showHide;  bool? isFeatured;  List<ProductImage>? productImage;  Api_models(      {this.id,        this.title,        this.banner,        this.description,        this.category,        this.colors,        this.price,        this.isAvailable,        this.showHide,        this.isFeatured,        this.productImage});  Api_models.fromJson(Map<String, dynamic> json) {    id = json['id'];    title = json['title'];    banner = json['banner'];    description = json['description'];    category = json['category'] != null        ? new Category.fromJson(json['category'])        : null;    if (json['colors'] != null) {      colors = <Colors>[];      json['colors'].forEach((v) {        colors!.add(new Colors.fromJson(v));      });    }    price = json['price'];    isAvailable = json['is_available'];    showHide = json['show_hide'];    isFeatured = json['is_featured'];    if (json['product_image'] != null) {      productImage = <ProductImage>[];      json['product_image'].forEach((v) {        productImage!.add(new ProductImage.fromJson(v));      });    }  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['id'] = this.id;    data['title'] = this.title;    data['banner'] = this.banner;    data['description'] = this.description;    if (this.category != null) {      data['category'] = this.category!.toJson();    }    if (this.colors != null) {      data['colors'] = this.colors!.map((v) => v.toJson()).toList();    }    data['price'] = this.price;    data['is_available'] = this.isAvailable;    data['show_hide'] = this.showHide;    data['is_featured'] = this.isFeatured;    if (this.productImage != null) {      data['product_image'] =          this.productImage!.map((v) => v.toJson()).toList();    }    return data;  }}class Category {  String? title;  Category({this.title});  Category.fromJson(Map<String, dynamic> json) {    title = json['title'];  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['title'] = this.title;    return data;  }}class Colors {  String? colorCode;  Colors({this.colorCode});  Colors.fromJson(Map<String, dynamic> json) {    colorCode = json['color_code'];  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['color_code'] = this.colorCode;    return data;  }}class ProductImage {  String? images;  ProductImage({this.images});  ProductImage.fromJson(Map<String, dynamic> json) {    images = json['images'];  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['images'] = this.images;    return data;  }}