import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'catigory_buttons_bar.dart';class CatigoryButtons extends StatelessWidget {  final String text;  final void Function() onPressed ;  const CatigoryButtons({    Key? key,    required this.text, required this.onPressed,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(top: 8, bottom: 8, right: 0, left: 0),      child: Container(          height: 50,          child: RawMaterialButton(            onPressed: () {              onPressed();            },            fillColor: Colors.white,            elevation: 0,            shape: RoundedRectangleBorder(                borderRadius: BorderRadius.circular(12)),            child: Text(              text,              style: GoogleFonts.inter(                  fontSize: 16,                  color: Colors.black,                  fontWeight: FontWeight.bold),            ),          )),    );  }}