import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';class image_name_profile extends StatelessWidget {  const image_name_profile({    Key? key, required this.name, required this.url,  }) : super(key: key);  final String name;  final String url;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(top: 30),      child: Column(        children: [          CircleAvatar(            radius: 85,            backgroundImage: NetworkImage(                url),          ),          SizedBox(            height: 20,          ),          Text(name,            style: GoogleFonts.inter(fontSize: 16,fontWeight:FontWeight.bold),          ),        ],      ),    );  }}