import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:google_fonts/google_fonts.dart';import 'package:image_picker/image_picker.dart';import 'package:unicons/unicons.dart';import 'dart:io';class image_name_profile extends StatefulWidget {  const image_name_profile({    Key? key,    required this.name,  }) : super(key: key);  final String name;  @override  State<image_name_profile> createState() => _image_name_profileState();}class _image_name_profileState extends State<image_name_profile> {  File? image;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(top: 30),      child: Column(        children: [          ClipRRect(              borderRadius: BorderRadius.circular(85),              child: Container(                width: 170,                height: 170,                decoration: BoxDecoration(                    color: Color.fromRGBO(69, 185, 238, 1),                    borderRadius: BorderRadius.circular(85)),                child: image != null                    ? Image.file(                        image!,                        fit: BoxFit.fill,                      )                    : Container(                        width: 120,                        height: 120,                        decoration: BoxDecoration(                            color: Color.fromRGBO(69, 185, 238, 1),                            shape: BoxShape.circle),                        child: IconButton(                          onPressed: () {                            _showDialog(context);                            print(image?.path);                            print("object");                          },                          icon: Icon(UniconsLine.image_upload),                        ),                      ),              )),          SizedBox(            height: 20,          ),          Text(            widget.name,            style: GoogleFonts.inter(fontSize: 16, fontWeight: FontWeight.bold),          ),        ],      ),    );  }  Future pickImage() async {    try {      final image = await ImagePicker().pickImage(source: ImageSource.gallery);      if (image == null) return;      final imageTemporary = File(image.path);      setState(() => this.image = imageTemporary);    } on PlatformException catch (e) {      print("Failed to pick image : $e");    }  }  Future pickImage_camera() async {    try {      final image = await ImagePicker().pickImage(source: ImageSource.camera);      if (image == null) return;      final imageTemporary = File(image.path);      setState(() => this.image = imageTemporary);    } on PlatformException catch (e) {      print("Failed to pick image : $e");    }  }  void _showDialog(BuildContext context) {    showDialog(      context: context,      builder: (BuildContext context) {        return AlertDialog(          title: Text(            "اختيار الصورة",            textDirection: TextDirection.rtl,          ),          content: Text(            "اختار صورة من : ",            textDirection: TextDirection.rtl,          ),          actions: [            MaterialButton(              child: Row(                mainAxisAlignment: MainAxisAlignment.spaceAround,                children: [                  Container(                      margin: EdgeInsets.only(bottom: 0, left: 0, right: 0),                      width: 100,                      height: 40,                      child: RawMaterialButton(                        onPressed: () async {                          await pickImage();                          Navigator.pop(                            context,                          );                        },                        fillColor: Color.fromRGBO(69, 185, 238, 1),                        shape: RoundedRectangleBorder(                            borderRadius: BorderRadius.circular(30)),                        child: Text(                          "المعرض",                          style: GoogleFonts.inter(                              fontSize: 15,                              color: Colors.black,                              fontWeight: FontWeight.bold),                        ),                      )),                  Container(                      margin: EdgeInsets.only(bottom: 0, left: 0, right: 0),                      width: 100,                      height: 40,                      child: RawMaterialButton(                        onPressed: () async {                          await pickImage_camera();                          Navigator.pop(                            context,                          );                        },                        fillColor: Color.fromRGBO(69, 185, 238, 1),                        shape: RoundedRectangleBorder(                            borderRadius: BorderRadius.circular(18)),                        child: Text(                          "الكامرة",                          style: GoogleFonts.inter(                              fontSize: 15,                              color: Colors.black,                              fontWeight: FontWeight.bold),                        ),                      )),                ],              ),              onPressed: () {                Navigator.of(context).pop();              },            ),          ],        );      },    );  }}