import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';class profile_button extends StatelessWidget {  const profile_button({    Key? key,    required this.title,    required this.icon_left,    required this.onPressed,  }) : super(key: key);  final String title;  final Widget icon_left;  final void Function() onPressed;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(bottom: 25),      child: TextButton(        style: ButtonStyle(          padding: MaterialStateProperty.all(const EdgeInsets.all(0)),          shape: MaterialStateProperty.all(RoundedRectangleBorder(            borderRadius: BorderRadius.circular(15),            side: BorderSide(width: 2, color: Color(0xff9AE1FF)),          )),          backgroundColor: MaterialStateProperty.all(Colors.transparent),          shadowColor: MaterialStateProperty.all(              Theme.of(context).colorScheme.onSurface),        ),        onPressed: () {          print("Account Settings1111");          onPressed();        },        child: Container(          width: double.infinity,          height: 60,          child: Padding(            padding: const EdgeInsets.all(10.0),            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              crossAxisAlignment: CrossAxisAlignment.center,              children: [                Row(                  children: [                    icon_left,                    SizedBox(                      width: 20,                    ),                    Text(                      title,                      style: GoogleFonts.inter(                          fontSize: 14,                          color: Colors.black,                          fontWeight: FontWeight.w700),                    ),                  ],                ),                Icon(                  Icons.arrow_forward_ios,                  color: Color(0xff45B9EE),                  size: 24,                ),              ],            ),          ),        ),      ),    );  }}