import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import '../help/text_style.dart';class profile_button extends StatelessWidget {  const profile_button({    Key? key,    required this.title,    required this.icon_left,    required this.onPressed,  }) : super(key: key);  final String title;  final Widget icon_left;  final void Function() onPressed;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(bottom: 25),      child: TextButton(        style: ButtonStyle(          padding: MaterialStateProperty.all(const EdgeInsets.all(0)),          shape: MaterialStateProperty.all(RoundedRectangleBorder(            borderRadius: BorderRadius.circular(15),            side: BorderSide(width: 2, color: Color(0xff9AE1FF)),          )),          backgroundColor: MaterialStateProperty.all(Colors.transparent),          shadowColor: MaterialStateProperty.all(              Theme.of(context).colorScheme.onSurface),        ),        onPressed: () {          print("Account Settings1111");          onPressed();        },        child: Container(          width: double.infinity,          height: 60,          child: Padding(            padding: const EdgeInsets.all(10.0),            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              crossAxisAlignment: CrossAxisAlignment.center,              children: [                Padding(                  padding: const EdgeInsets.only(left: 10),                  child: Icon(                    Icons.arrow_back_ios,                    color: Color(0xff45B9EE),                    size: 24,                  ),                ),                Row(                  children: [                    Text(                      title,                      style: Text_Style.getstyle(fontsize: 18, ColorText: Colors.black, fontWeight: FontWeight.w600),                    ),                    SizedBox(                      width: 20,                    ),                    icon_left,                  ],                ),              ],            ),          ),        ),      ),    );  }}