ing(                padding: const EdgeInsets.only(left: 25, right: 25, top: 30),                child: Column(                  mainAxisAlignment: MainAxisAlignment.start,                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    profile_button(                      title: 'Favorite',                      icon_left: Icon(                        Icons.favorite,                        color: Color(0xff45B9EE),                        size: 24,                      ),                      onPressed: () {                        print("Favorite");                      },                    ),                    profile_button(                      title: 'Orders',                      icon_left: Icon(                        Icons.settings,                        color: Color(0xff45B9EE),                        size: 24,                      ),                      onPressed: () {                        print("Orders");                      },                    ),                    profile_button(                      title: 'About us',                      icon_left: Icon(                        Icons.info_outline_rounded,                        color: Color(0xff45B9EE),                        size: 24,                      ),                      onPressed: () {                        print("About us");                      },                    ),                    profile_button(                      title: 'Loge out',                      icon_left: Icon(                        Icons.exit_to_app,                        color: Color(0xff45B9EE),                        size: 24,                      ),                      onPressed: () {                        print("Loge out");                      },                    ),                  ],                ),              )            ]),      ),      bottomNavigationBar: SnakeNavigationBar.color(       