import 'package:flutter/material.dart';import 'package:url_launcher/url_launcher.dart';import 'package:url_launcher/url_launcher.dart';import 'package:url_launcher/url_launcher_string.dart';import 'package:whatsapp/whatsapp.dart';import 'dart:io';class MyAppw extends StatefulWidget {  const MyAppw({Key? key}) : super(key: key);  @override  State<MyAppw> createState() => _MyAppwState();}class _MyAppwState extends State<MyAppw> {  @override  Whatsapp whatsapp = Whatsapp();  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: [            ElevatedButton(              child: const Text('Send Message11'),              onPressed: () async {                sendWhatsAppMessage(                    phone: '+9647835130173',                    message:                    'Hello WhatsApp From Hoomy app');              },            ),          ],        ),      ),    );  }  void sendWhatsAppMessage({    required String phone,    required String message,  }) async {    String url() {      if (Platform.isIOS) {        return "whatsapp://wa.me/$phone/?text=${Uri.parse(message)}";      } else {        return "whatsapp://send?phone=$phone&text=$message";      }    }    await canLaunchUrl(Platform.script)        ? launchUrl(Uri.http(url()))        : ScaffoldMessenger.of(context).showSnackBar(SnackBar(        backgroundColor: Colors.red,        content: Text('There is no WhatsApp on your Device!')));  }}